import React, { useRef, useEffect } from "react";
import { useParams } from "react-router-dom";
import { VncScreen } from "react-vnc";
import "./style.scss";

const VncPage = () => {
  const { vns_key } = useParams(); // Использование параметра маршрута
  const ref = useRef();

  useEffect(() => {
    const rfb = ref.current?.rfb;

    return () => {
      // Чистка соединения при размонтировании компонента
      if (rfb) {
        rfb.disconnect(); // Корректно закрываем соединение
      }
    };
  }, []);

  const vncUrl = `https://dd-api.ibm.kg/api2/json/nodes/host19/qemu/${vns_key}/vncwebsocket?port=5900&vncticket=PVEVNC:66D70074::`;

  return (
    <VncScreen
      url={vncUrl}
      scaleViewport
      background="#000000"
      style={{ width: "100vw", height: "100vh" }}
      ref={ref}
    />
  );
};

export default VncPage;
